
def patternCount(dnaText, pattern):
    count = 0
    for i in xrange(0, len(dnaText) - len(pattern)):
        word = dnaText[i:i+len(pattern)]
        if (word == pattern):
            count = count + 1
    return count

def freqWordProblem(text, k):
    countWords = []
    for i in xrange(0, len(text) - k):
        pattern = text[i:i+k]
        countWords.append(patternCount(text, pattern))

    maxCount = 0
    indexes = []
    for j in xrange(0, len(countWords)):
        count = countWords[j]
        if (count == maxCount):
            indexes.append(j)
        elif (count > maxCount):
            indexes = [j]
            maxCount = count

    result = set()
    for index in indexes:
        result.add(text[index:index+k])

    return list(result)

mapDNA = {
    "A": "T",
    "G": "C",
    "T": "A",
    "C": "G"
}
def complimentDNA(text):
    result = ""
    for letter in text:
        result = result + mapDNA[letter]
    return result[::-1]

def patternFind(text, pattern):
    index = []
    for i in xrange(0,len(text)-len(pattern)):
        word = text[i:i+len(pattern)]
        if word == pattern:
            index.append(i)
    return index

for word in patternFind("","GCGCCACGC"):
    print(word)
